// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title ERC721NFT
 * @dev Complete ERC721 NFT Collection Contract with Minting, Royalties, and Access Control
 */
contract ERC721NFT is ERC721, ERC721URIStorage, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;

    string public baseTokenURI;
    uint256 public maxSupply;
    uint256 public mintPrice;
    uint256 public royaltyPercentage; // basis points (250 = 2.5%)
    address public royaltyRecipient;
    bool public isPublicMintEnabled;

    mapping(address => bool) public whitelist;
    mapping(uint256 => string) private _tokenURIs;

    event NFTMinted(address indexed minter, uint256 indexed tokenId, string tokenURI);
    event BaseURIUpdated(string newBaseURI);
    event MintPriceUpdated(uint256 newPrice);
    event PublicMintToggled(bool enabled);
    event Withdrawn(address indexed recipient, uint256 amount);

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _baseTokenURI,
        uint256 _maxSupply,
        uint256 _royaltyPercentage,
        address _royaltyRecipient,
        uint256 _mintPrice,
        bool _isPublicMint,
        address _owner
    ) ERC721(_name, _symbol) {
        baseTokenURI = _baseTokenURI;
        maxSupply = _maxSupply;
        royaltyPercentage = _royaltyPercentage;
        royaltyRecipient = _royaltyRecipient;
        mintPrice = _mintPrice;
        isPublicMintEnabled = _isPublicMint;

        // Transfer ownership to specified address
        _transferOwnership(_owner);

        // Add owner to whitelist
        whitelist[_owner] = true;
    }

    /**
     * @dev Mint a new NFT
     */
    function mint(string memory tokenURI)
        external
        payable
        nonReentrant
        returns (uint256)
    {
        require(
            isPublicMintEnabled || whitelist[msg.sender] || msg.sender == owner(),
            "Minting not allowed"
        );
        require(
            maxSupply == 0 || _tokenIds.current() < maxSupply,
            "Max supply reached"
        );
        require(msg.value >= mintPrice, "Insufficient payment");

        _tokenIds.increment();
        uint256 newTokenId = _tokenIds.current();

        _safeMint(msg.sender, newTokenId);
        _setTokenURI(newTokenId, tokenURI);

        emit NFTMinted(msg.sender, newTokenId, tokenURI);

        return newTokenId;
    }

    /**
     * @dev Batch mint NFTs (only owner)
     */
    function batchMint(address[] memory recipients, string[] memory tokenURIs)
        external
        onlyOwner
        nonReentrant
    {
        require(recipients.length == tokenURIs.length, "Array length mismatch");
        require(
            maxSupply == 0 || _tokenIds.current() + recipients.length <= maxSupply,
            "Would exceed max supply"
        );

        for (uint256 i = 0; i < recipients.length; i++) {
            _tokenIds.increment();
            uint256 newTokenId = _tokenIds.current();

            _safeMint(recipients[i], newTokenId);
            _setTokenURI(newTokenId, tokenURIs[i]);

            emit NFTMinted(recipients[i], newTokenId, tokenURIs[i]);
        }
    }

    /**
     * @dev Set base URI for all tokens
     */
    function setBaseURI(string memory _baseTokenURI) external onlyOwner {
        baseTokenURI = _baseTokenURI;
        emit BaseURIUpdated(_baseTokenURI);
    }

    /**
     * @dev Update mint price
     */
    function setMintPrice(uint256 _mintPrice) external onlyOwner {
        mintPrice = _mintPrice;
        emit MintPriceUpdated(_mintPrice);
    }

    /**
     * @dev Toggle public minting
     */
    function togglePublicMint(bool _enabled) external onlyOwner {
        isPublicMintEnabled = _enabled;
        emit PublicMintToggled(_enabled);
    }

    /**
     * @dev Add addresses to whitelist
     */
    function addToWhitelist(address[] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = true;
        }
    }

    /**
     * @dev Remove addresses from whitelist
     */
    function removeFromWhitelist(address[] memory addresses) external onlyOwner {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = false;
        }
    }

    /**
     * @dev Get total supply
     */
    function totalSupply() external view returns (uint256) {
        return _tokenIds.current();
    }

    /**
     * @dev Withdraw contract balance
     */
    function withdraw() external onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        require(balance > 0, "No balance to withdraw");

        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Withdrawal failed");

        emit Withdrawn(owner(), balance);
    }

    /**
     * @dev EIP-2981 royalty info
     */
    function royaltyInfo(uint256 , uint256 salePrice)
        external
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        royaltyAmount = (salePrice * royaltyPercentage) / 10000;
        receiver = royaltyRecipient;
    }

    /**
     * @dev Override _baseURI
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    /**
     * @dev Override tokenURI to return custom URI if set
     */
    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    /**
     * @dev Override _burn
     */
    function _burn(uint256 tokenId)
        internal
        virtual
        override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }

    /**
     * @dev Check interface support
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/NFTMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title NFTMarketplace
 * @dev Core NFT Marketplace for ERC721 and ERC1155 with royalty support
 */
contract NFTMarketplace is ReentrancyGuard, Ownable {

    address payable public feeRecipient;
    uint256 public feePercentage; // basis points (250 = 2.5%)

    struct Listing {
        address seller;
        uint256 price;
        bool active;
        uint256 royaltyPercentage;
        address royaltyRecipient;
    }

    // nftContract => tokenId => Listing
    mapping(address => mapping(uint256 => Listing)) public listings;
    mapping(address => bool) public supportedTokenStandards;

    event ItemListed(
        address indexed nftContract,
        uint256 indexed tokenId,
        address indexed seller,
        uint256 price
    );

    event ItemSold(
        address indexed nftContract,
        uint256 indexed tokenId,
        address indexed buyer,
        address seller,
        uint256 price,
        uint256 marketplaceFee,
        uint256 royaltyFee
    );

    event ItemCanceled(
        address indexed nftContract,
        uint256 indexed tokenId,
        address indexed seller
    );

    constructor(address payable _feeRecipient, uint256 _feePercentage) {
        require(_feeRecipient != address(0), "Invalid fee recipient");
        require(_feePercentage <= 1000, "Fee too high");

        feeRecipient = _feeRecipient;
        feePercentage = _feePercentage;
    }

    function listItem(
        address nftContract,
        uint256 tokenId,
        uint256 price,
        uint256 royaltyPercentage,
        address royaltyRecipient
    ) external nonReentrant {
        require(price > 0, "Price must be greater than zero");
        require(royaltyPercentage <= 300, "Royalty percent too high");

        IERC721 nft = IERC721(nftContract);
        require(nft.ownerOf(tokenId) == msg.sender, "Not token owner");
        require(
            nft.getApproved(tokenId) == address(this) ||
            nft.isApprovedForAll(msg.sender, address(this)),
            "Contract not approved"
        );

        nft.transferFrom(msg.sender, address(this), tokenId);

        listings[nftContract][tokenId] = Listing({
            seller: msg.sender,
            price: price,
            active: true,
            royaltyPercentage: royaltyPercentage,
            royaltyRecipient: royaltyRecipient
        });

        emit ItemListed(nftContract, tokenId, msg.sender, price);
    }

    function buyItem(address nftContract, uint256 tokenId)
        external
        payable
        nonReentrant
    {
        Listing memory listing = listings[nftContract][tokenId];

        require(listing.active, "Item not for sale");
        require(msg.value >= listing.price, "Insufficient payment");

        listings[nftContract][tokenId].active = false;

        uint256 marketplaceFee = (listing.price * feePercentage) / 10000;
        uint256 royaltyFee = 0;

        if (listing.royaltyRecipient != address(0) && listing.royaltyPercentage > 0) {
            royaltyFee = (listing.price * listing.royaltyPercentage) / 10000;
        }

        uint256 sellerProceeds = listing.price - marketplaceFee - royaltyFee;

        IERC721(nftContract).transferFrom(address(this), msg.sender, tokenId);

        (bool feeSuccess, ) = feeRecipient.call{value: marketplaceFee}("");
        require(feeSuccess, "Fee transfer failed");

        if (royaltyFee > 0) {
            (bool royaltySuccess, ) = payable(listing.royaltyRecipient).call{value: royaltyFee}("");
            require(royaltySuccess, "Royalty transfer failed");
        }

        (bool sellerSuccess, ) = payable(listing.seller).call{value: sellerProceeds}("");
        require(sellerSuccess, "Seller payment failed");

        if (msg.value > listing.price) {
            (bool refundSuccess, ) = payable(msg.sender).call{value: msg.value - listing.price}("");
            require(refundSuccess, "Refund failed");
        }

        emit ItemSold(
            nftContract,
            tokenId,
            msg.sender,
            listing.seller,
            listing.price,
            marketplaceFee,
            royaltyFee
        );
    }

    function cancelListing(address nftContract, uint256 tokenId)
        external
        nonReentrant
    {
        Listing memory listing = listings[nftContract][tokenId];

        require(listing.active, "Item not listed");
        require(listing.seller == msg.sender, "Only seller can cancel");

        listings[nftContract][tokenId].active = false;

        IERC721(nftContract).transferFrom(address(this), msg.sender, tokenId);

        emit ItemCanceled(nftContract, tokenId, msg.sender);
    }

    function updatePrice(address nftContract, uint256 tokenId, uint256 newPrice)
        external
    {
        Listing storage listing = listings[nftContract][tokenId];

        require(listing.active, "Item not listed");
        require(listing.seller == msg.sender, "Only seller can update");
        require(newPrice > 0, "Price must be greater than zero");

        listing.price = newPrice;
    }

    function getListing(address nftContract, uint256 tokenId)
        external
        view
        returns (
            address seller,
            uint256 price,
            bool active,
            uint256 royaltyPercentage,
            address royaltyRecipient
        )
    {
        Listing memory listing = listings[nftContract][tokenId];
        return (
            listing.seller,
            listing.price,
            listing.active,
            listing.royaltyPercentage,
            listing.royaltyRecipient
        );
    }

    function updateFeePercentage(uint256 newFeePercentage) external onlyOwner {
        require(newFeePercentage <= 1000, "Fee too high");
        feePercentage = newFeePercentage;
    }

    function updateFeeRecipient(address payable newFeeRecipient) external onlyOwner {
        require(newFeeRecipient != address(0), "Invalid recipient");
        feeRecipient = newFeeRecipient;
    }

    function updateSupportedTokenStandard(address nftContract, bool supported)
        external
        onlyOwner
    {
        supportedTokenStandards[nftContract] = supported;
    }

    function isListed(address nftContract, uint256 tokenId)
        external
        view
        returns (bool)
    {
        return listings[nftContract][tokenId].active;
    }

    function calculateFees(uint256 price, uint256 royaltyPercentage)
        external
        view
        returns (
            uint256 marketplaceFee,
            uint256 royaltyFee,
            uint256 sellerAmount
        )
    {
        marketplaceFee = (price * feePercentage) / 10000;
        royaltyFee = (price * royaltyPercentage) / 10000;
        sellerAmount = price - marketplaceFee - royaltyFee;
    }

    function withdrawBalance() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No balance to withdraw");

        (bool success, ) = feeRecipient.call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "Emergency withdrawal failed");
    }

    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return this.onERC721Received.selector;
    }

    function supportsInterface(bytes4 interfaceId) external pure returns (bool) {
        return interfaceId == 0x01ffc9a7 || // ERC165
               interfaceId == 0x150b7a02;   // ERC721TokenReceiver
    }

    receive() external payable {}
}

